svg.script:
var currentDrag;
    function inspect(obj) {
        var str = new Array();
        var element = null;
        for(element in obj) { str[str.length] = element; }
        str.sort();
        alert(obj + ":" + str.join(' '));
    }

    var bMouseDragging = false;
    var nMouseOffsetX = 0;
    var nMouseOffsetY = 0;
    var background = document.getElementById("background");

    function mouseDown(evt) { 
        bMouseDragging = true;
       currentDrag =evt.target.id  

            if(evt.target.id!="background"){
            var obj =document.getElementById(evt.target.id);
            var p = document.documentElement.createSVGPoint();
            p.x = evt.clientX;
            p.y = evt.clientY;

             var m = obj.getScreenCTM();
            p = p.matrixTransform(m.inverse());

            nMouseOffsetX = p.x - parseInt(obj.getAttribute("cx"));
            nMouseOffsetY = p.y - parseInt(obj.getAttribute("cy"));
   }
    }
    function mouseUp(evt) { 
        bMouseDragging = false;
    }
    function mouseMove(evt) { 

      //console.log(evt.target.id)  
        var p = document.documentElement.createSVGPoint();
        p.x = evt.clientX;
        p.y = evt.clientY;
        var bClient = true;

        if(bMouseDragging) {

            var obj = document.getElementById(currentDrag);

        if(obj){
                var m = obj.getScreenCTM();
                p = p.matrixTransform(m.inverse());

                obj.setAttribute("cx", p.x - nMouseOffsetX);
                obj.setAttribute("cy", p.y - nMouseOffsetY);
                bClient = false;
            }
      }


    }

    function displayCoords(x,y,extra) {
        var xNode = document.getElementById("xpos");
        var yNode = document.getElementById("ypos");
        if(xNode && yNode) {
            xNode.firstChild.nodeValue = parseInt(x) + extra;
            yNode.firstChild.nodeValue = parseInt(y) + extra;
        }
    }

    function displayRawText(text) {
        var textNode = document.getElementById("raw");
        if(textNode) {
            textNode.firstChild.nodeValue = text;
        }
    }

    function init() {
        var ball = document.getElementById("ball");
        if(ball) {
            ball.addEventListener("mousedown", mouseDown, false);
            ball.addEventListener("mouseup", mouseUp, false);
            ball.addEventListener("mousemove", mouseMove, false);
        }


     var ball = document.getElementById("sun");
        if(ball) {
            ball.addEventListener("mousedown", mouseDown, false);
            ball.addEventListener("mouseup", mouseUp, false);
            ball.addEventListener("mousemove", mouseMove, false);
        }

        displayRawText("Drag the ball around");
    }
